import React, { useState } from "react";
import axios from "axios";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:5000/api/auth/login",
        { email, password }
      );
      alert("Login Successful!");
      console.log(response.data); // Process the token or user data
    } catch (error) {
      console.error(error);
      alert("Login Failed!");
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gradient-to-r from-yellow-100 to-green-200 relative">
      <div className="absolute top-0 right-0 p-8">
        <img
          src="D:\CampusGrid\client\public\background_image.png"
          alt="Illustration"
          className="w-96"
        />
      </div>
      <div className="w-full max-w-md p-8 bg-white shadow-md rounded-lg z-10">
        <h1 className="text-2xl font-bold text-center text-gray-800">Login</h1>
        <form onSubmit={handleLogin} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Email</label>
            <input
              type="email"
              placeholder="username@gmail.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className="w-full px-3 py-2 mt-1 border rounded-lg shadow-sm focus:outline-none focus:ring focus:ring-green-300"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Password</label>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="w-full px-3 py-2 mt-1 border rounded-lg shadow-sm focus:outline-none focus:ring focus:ring-green-300"
            />
          </div>
          <div className="text-right">
            <a href="#" className="text-sm text-green-600 hover:underline">
              Forgot Password?
            </a>
          </div>
          <button
            type="submit"
            className="w-full px-4 py-2 text-white bg-blue-600 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-4 focus:ring-blue-300"
          >
            Sign in
          </button>
          <div className="text-center text-sm text-gray-600">or continue with</div>
          <button
            type="button"
            className="w-full px-4 py-2 text-gray-700 bg-gray-100 border rounded-lg hover:bg-gray-200 focus:outline-none focus:ring-4 focus:ring-gray-300"
          >
            Sign in with Google
          </button>
          <div className="text-center text-sm text-gray-600">
            Don't have an account yet?{' '}
            <a href="#" className="text-green-600 hover:underline">
              Register
            </a>
          </div>
        </form>
      </div>
      <div className="absolute bottom-0 left-0 p-8">
        <img
          src="D:\CampusGrid\client\public\logo192.png"
          alt="Campus Grid Logo"
          className="w-32"
        />
      </div>
    </div>
  );
};

export default LoginPage;
